using RTC.Interface; 
using RTC.Base; 
using System; 
using System.Collections.Generic; 
using System.Reflection; 
using System.Text.RegularExpressions; 
using RTC.Core.Extension; 

namespace RTC.Core 
{ 
    internal sealed class Parser : AbstractParser 
    { 
        public override ParserResult Parse(AnalyzerResult result, ITypeConverterProvider convertersProvider, ParameterInfo[] cmdParams) 
        { 
            string cmd = result.Parameters; 

            List<string> errors = new List<string>(); 
            List<object> convertedParameters = new List<object>(); 

            foreach (var param in cmdParams) 
            { 
                var converter = convertersProvider.Get(param.ParameterType); 

                if (converter == null) 
                { 
                    errors.Add(string.Format("No converter found for type: {0}", param.ParameterType.FullName)); 
                    break; 
                } 

                cmd = cmd.Trim(); 
                var ma = Regex.Match(cmd, string.Format("^{0}", converter.Regex)); 

                if (ma.Value.IsNullOrWhiteSpace()) 
                { 
                    if (param.IsOptional) 
                    { 
                        convertedParameters.Add(param.DefaultValue); 
                        continue; 
                    } 

                    errors.Add("Invalid parameters."); 
                    break; 
                } 
                else 
                { 
                    try 
                    { 
                        convertedParameters.Add(converter.ConvertFrom(ma.Value)); 
                    } 
                    catch (Exception ex) 
                    { 
                        errors.Add(string.Format("Failed to convert '{0}' to type {1}.{2}{3}", ma.Value, param.ParameterType.Name, Environment.NewLine, ex.Message)); 
                        break; 
                    } 

                    cmd = cmd.Substring(ma.Length); 
                } 
            } 

            if (cmd.TrimEnd().Length > 0 && errors.Count == 0) 
            { 
                errors.Add("Provided parameters do not match with the expected parameters."); 
            } 

            return new ParserResult(convertedParameters.ToArray(), errors.ToArray()); 
        } 
    } 
}
